arcades = Arcade.all
return puts 'No arcades found!' if arcades.empty?

arcades.each do |arcade|
  games_to_create = GAMES_DATA.sample(rand(10..15))
  
  games_to_create.each do |game_data|
    next if arcade.games.exists?(name: game_data[:name])

    arcade.games.create!(
      name: game_data[:name],
      genre: game_data[:genre],
      developer: game_data[:developer],
      release_year: game_data[:release_year],
      description: game_data[:description],
      rating: rand(30..50) / 10.0
    )
  end

  puts "Created #{games_to_create.size} games for #{arcade.name}"
end
